import React, { useEffect, useState } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import './PDFViewer.css';

// ËÆæÁΩÆPDF.js workerÔºà‰ΩøÁî®Êú¨Âú∞Êñá‰ª∂ÔºåÈÅøÂÖçCDNË¢´Êã¶Êà™Ôºâ
pdfjs.GlobalWorkerOptions.workerSrc = '/pdf.worker.min.js';

const PDFViewer = ({ onBack }) => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [useBackup, setUseBackup] = useState(false);
  const [isMobile, setIsMobile] = useState(false);
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [scale, setScale] = useState(1.0);

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
    setLoading(false);
    setError(null);
  };

  const onDocumentLoadError = (error) => {
    console.error('PDFÂä†ËΩΩÈîôËØØ:', error);
    if (!useBackup) {
      setUseBackup(true);
      setLoading(true);
      setError(null);
    } else {
      setError('PDFÂä†ËΩΩÂ§±Ë¥•ÔºåËØ∑Ê£ÄÊü•Êñá‰ª∂Ë∑ØÂæÑ„ÄÇÂ¶ÇÊûúÈóÆÈ¢òÊåÅÁª≠Â≠òÂú®ÔºåËØ∑ËÅîÁ≥ªÊäÄÊúØÊîØÊåÅ„ÄÇ');
      setLoading(false);
    }
  };

  // Ê°åÈù¢Á´Ø‰ΩøÁî®ÊµèËßàÂô®ÂÜÖÁΩÆÊü•ÁúãÂô®ÔºõÁßªÂä®Á´Ø‰ΩøÁî® react-pdf Ê∏≤Êüì

  const goToPrevPage = () => setPageNumber(prev => Math.max(prev - 1, 1));
  const goToNextPage = () => setPageNumber(prev => Math.min(prev + 1, numPages || 1));
  const zoomIn = () => setScale(prev => Math.min(prev + 0.2, 3));
  const zoomOut = () => setScale(prev => Math.max(prev - 0.2, 0.5));
  const resetZoom = () => setScale(1.0);

  const handleDownload = () => {
    const link = document.createElement('a');
    link.href = "/robot-brochure.pdf";
    link.download = "Ôºà1ÔºâÈößÈÅìÁªºÂêàÈáèÊµãÊú∫Âô®‰∫∫(‰∫ßÂìÅÂÆ£‰º†ÂÜå).pdf";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  useEffect(() => {
    const ua = navigator.userAgent || navigator.vendor || window.opera;
    const mobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(ua);
    setIsMobile(mobile);
  }, []);

  return (
    <div className="pdf-viewer">
      <div className="pdf-header">
        <h3>ÈößÈÅìÁªºÂêàÈáèÊµãÊú∫Âô®‰∫∫ - ‰∫ßÂìÅÂÆ£‰º†ÂÜå</h3>
        {useBackup && (
          <p className="backup-notice">
            üìÑ Ê≠£Âú®‰ΩøÁî®Â§áÁî®PDFÊñá‰ª∂
          </p>
        )}
        <div className="pdf-controls">
          {/* {isMobile && (
            <button className="control-btn" onClick={onBack}>‚Üê ËøîÂõû</button>
          )} */}
          {isMobile ? (
            <>
              <div className="page-controls">
                <button className="control-btn" onClick={goToPrevPage} disabled={pageNumber <= 1}>‚Üê ‰∏ä‰∏ÄÈ°µ</button>
                <span className="page-info">{pageNumber} / {numPages || '--'}</span>
                <button className="control-btn" onClick={goToNextPage} disabled={numPages ? pageNumber >= numPages : true}>‰∏ã‰∏ÄÈ°µ ‚Üí</button>
              </div>
              <div className="zoom-controls">
                <button className="control-btn" onClick={zoomOut}>üîç-</button>
                <span className="zoom-info">{Math.round(scale * 100)}%</span>
                <button className="control-btn" onClick={zoomIn}>üîç+</button>
                <button className="control-btn" onClick={resetZoom}>ÈáçÁΩÆ</button>
              </div>
            </>
          ) : (
            <div className="zoom-controls">
              <button className="control-btn" onClick={() => window.open(useBackup ? '/sample.pdf' : '/robot-brochure.pdf', '_blank')}>Âú®Êñ∞Á™óÂè£ÊâìÂºÄ</button>
              <button className="control-btn" onClick={handleDownload}>‰∏ãËΩΩPDF</button>
            </div>
          )}
        </div>
      </div>

      <div className="pdf-container">
        {isMobile ? (
          <div className="pdf-document">
            <Document
              file={useBackup ? '/sample.pdf' : '/robot-brochure.pdf'}
              onLoadSuccess={onDocumentLoadSuccess}
              onLoadError={onDocumentLoadError}
              options={{ cMapUrl: '/cmaps/', cMapPacked: true, standardFontDataUrl: '/standard_fonts/' }}
              loading={
                <div className="loading-container">
                  <div className="loading-spinner"></div>
                  <p>Ê≠£Âú®Âä†ËΩΩPDFÊñá‰ª∂...</p>
                </div>
              }
            >
              <div style={{ width: '100%', overflow: 'hidden' }}>
                <Page
                  pageNumber={pageNumber}
                  width={Math.min(window.innerWidth - 48, 800)}
                  scale={isMobile ? undefined : scale}
                  renderTextLayer={false}
                  renderAnnotationLayer={false}
                />
              </div>
            </Document>
          </div>
        ) : (
          <div className="pdf-document">
            <iframe
              title="brochure"
              className="pdf-iframe"
              src={(useBackup ? '/sample.pdf' : '/robot-brochure.pdf') + '#view=fitH'}
              onLoad={() => onDocumentLoadSuccess({ numPages })}
            />
          </div>
        )}
      </div>

    </div>
  );
};

export default PDFViewer;
